<?php require 'bootstrap.php';$dql="SELECT a FROM Aposta a GROUP BY a.opcaoCadastrada";$query=$entityManager->createQuery($dql);$apostas=$query->getResult();foreach ($apostas as $aposta){	$aux= $aposta->getOpcaoCadastrada();	$dqlcount="SELECT COUNT(a.jogo) FROM Aposta a WHERE a.jogo=".$aposta->getJogo()->getCodJogo().	"AND a.opcaoCadastrada='$aux'";		$querycount = $entityManager->createQuery($dqlcount);	$total=$querycount->getSingleScalarResult();echo "numero do jogo ".$total." ".$aposta->getOpcaoCadastrada()."<br>";	}?>
